 "ExecutableGrammar",
 "_ = (\r	^char: \"_\"\r)",
 "stringBody = (\r\r\r(* As an optimization, we process the body of a string with a dedicated scanning parser.\rIt should be equivalent to:\r\r  ^(self character | self aWhitespaceChar | (self char: \"\"\" ) | self twoQuotes) star *)\r\r	^CollectingCommentParser new  termBlock: \r		[:input | \r		| c = input peek. |\r		c isNil \r			ifTrue: [false (* let main routine handle end of input *)]\r			ifFalse: [c = 39 (* $' asInteger *)\r				ifFalse: [false]\r				ifTrue: [\r					| pos | \r					pos: input position.\r					input next.\r					input peek = 39\r						ifTrue: [false] \r						ifFalse: [input position: pos. true]]]]\r)",
 "class NSCommentParser = CombinatorialParser (\r(* As an optimization, we handle comments with a dedicated parser. It should be equivalent to:\r\rbeginComment = (char: \"(\"), (char: \"*\" ).\rendComment = (char: \"*\" ), (char: \")\").\rmetadataTag = (char: $:), id, (char: $:).\rany = charBetween: (Character value: 0) and: (Character value: 16r10FFFF).\rcomment = beginComment, \r	metadataTag opt, \r	((endComment not, any) | comment) star,\r	endComment.\r	\r*))",
 "CombinatorialParser",
 "CombinatorialParser",
 "public parse: input <ReadStream> inContext: context ifError: blk = (\r	| pos = input position. body = OrderedCollection new. |\r	(input atEnd not and: [input next = 40 (* ( *) and: [input atEnd not and: [input next = 42 (* * *)]]]) ifFalse: \r		[input position: pos.\r		^blk value: '(* expected' value: pos].\r	\r	[ | c |\r	input atEnd ifTrue: \r		[input position: pos.\r		^blk value: '(* expected' value: pos].\r	c:: input next.\r	body add: c.\r	c = 40 (* ( *) ifTrue: \r		[ | d |\r		input atEnd ifTrue: \r			[input position: pos.\r			^blk value: '(* expected' value: pos].\r		d:: input next.\r		d = 42 (* * *) ifTrue: [\r			| res |\r			body removeLast. (* not part of comment body *)\r			input position: input position - 2.\r			res:: self parse: input inContext: context ifError: \r				[:msg :pos2 |\r				input position: pos.\r				^blk value: '(* expected 2' value: pos].\r			1 to: res token size do: \r				[:index | body add: (res token runeAt: index)].\r		] ifFalse: [\r			body add: d.\r		]].\r	c = 42 (* * *) ifTrue: \r		[ | d |\r		input atEnd ifTrue: \r			[input position: pos.\r			^blk value: '(* expected' value: pos].\r		d:: input next.\r		d = 41 (* \")\" *) ifTrue: [\r			body removeLast. (* not part of comment body *)\r			^Token new\r				for: (String fromRunes: body)\r				start: pos end: input position]\r		ifFalse: [\r			input position: input position - 1. (* Consider next char as possible end star-paren *)\r		]].\r	c = nil ifTrue: \r		[input position: pos.\r		^blk value: '(* expected' value: pos].\r	] repeat.\r)",
 "public class TypedNS3Grammar = NS3Grammar ((* A parser for Strongtalk syntax. Adds productions for the Strongtalk type expression sublanguage, and the changes needed to use types in Newsqueak code. *)|                   \r\r arg = tokenFromSymbol: #arg.\r for = tokenFromSymbol: #for.\r generic = tokenFromSymbol: #generic.\r inheritedTypeOf = tokenFromSymbol: #inheritedTypeOf.\r is = tokenFromSymbol: #is.\r mssg = tokenFromSymbol: #message.\r of = tokenFromSymbol: #of.\r\r receiverType = tokenFromSymbol: #receiverType.\r subtypeOf = tokenFromSymbol: #subtypeOf.\r typeArg = tokenFromSymbol: #typeArg.\r where = tokenFromSymbol: #where.\r returnType = hat, type.\r\r                                                                 (* Type Grammar *)\r                                                                 \r  type = langleBracket, typeExpr,  rangleBracket.\r  typePrimary = identifier, typeArguments opt.\r  typeFactor =  typePrimary |\r                   (* blockType | *) newBlockType |\r                   tupleType |\r                   parenthesizedTypeExpression.\r  parenthesizedTypeExpression =  lparen,  typeExpr,  rparen.\r  typeTerm = typeFactor, identifier star.\r  typeExpr = typeTerm, ((vbar | semicolon | slash), typeExpr) opt.\r  typeArguments = lbracket, (typeExpr plusSeparatedBy: comma),  rbracket. \r  blockType =  lbracket, blockTypeArgList,  rbracket.\r  tupleType = lcurly, (typeExpr starSeparatedBy: dot), rcurly.\r  nonEmptyBlockTypeArgList = (typeExpr plusSeparatedBy: comma), \r                                     (comma, blockReturnType) opt.\r  blockTypeArgList =  nonEmptyBlockTypeArgList |\r                                   blockReturnType opt.\r  blockReturnType =  hat, typeExpr.\r\r  newBlockArgType = colon, typeTerm.\r  newBlockReturnType = typeExpr.\r  newNonEmptyBlockArgList = newBlockArgType plus, (vbar, newBlockReturnType) opt.\r  newBlockType = lbracket, (newNonEmptyBlockArgList | newBlockReturnType opt), rbracket.\r                                        \r                                         (* Type inference *)\r                                         \r  typePattern = langleBracket, typeFormal, (semicolon, typeFormal) star,  rangleBracket. (* changed from {} to <> brackets to avoid conflict with tuples *)\r  typeFormal =  where, identifier, typeParamConstraint opt, is, inferenceClause.\r  typeParamConstraint =  langleBracket,  typeBoundQualifier opt,  typeExpr, rangleBracket.\r  typeBoundQualifier =  subtypeOf | inheritedTypeOf.\r  inferenceClause = receiverType | \r                                      (returnType, returnTypeInferenceClause) |\r                                      typeArgInferenceClause |\r                                      (arg,  number, (of, msgSelector) opt).\r  returnTypeInferenceClause =  of, msgSelector.\r  msgSelector =  symbolConstant, mssg, of, inferenceClause.\r  typeArgInferenceClause = typeArg, number, for, generic, symbolConstant, of, inferenceClause. \r |)",
 "NS3Grammar",
 "NS3Grammar",
 "messagePattern = (\r     ^super messagePattern, returnType opt, typePattern opt. (* Type annotations *)\r  )",
 "slotDecl = (\r    ^super slotDecl , type opt.\r  )",
 "class UnarySelectorParser = CombinatorialParser ((* In the absence of a separate lexer, an ambiguity arises, which this parser deals with.\r The problem is that when parsing a unary expression that is an argument to a keyword\r message, one expects a unary selector, which is an identifer.  However, it may be that the next\r token is actually a keyword, part of the surrounding message. If we aren't actually tokenizing,\r the prefix of the keyword will be misinterpreted as an identifier and hence as another unary\r message being sent to the argument.\r \r Using a lexer solves this but introduces a subtlety around the assignment operator :=. In that case\r if there is no whitespace between a variable name and the assignment, the variable name will\r be tokenized as a keyword rather than as an identifier. The Strongtalk parser, DeltaParser, deals\r with this specially. In the longterm, that is probably the way to go. *)| p |)",
 "CombinatorialParser",
 "CombinatorialParser",
 "public on: s = (\r	p: s.\r)",
