 "collapsedHeadingDefinition = (\r	^link: subject name action: [enterSubject:: ClassSubject onModel: subject classMirror (* Why do we need a copy? *)].\r)",
 "crToLf: string = (\r	|\r	cr = [String fromCharCode: 13] on: Error do: [String value: 13].\r	lf = [String fromCharCode: 10] on: Error do: [String value: 10].\r	|\r	^string replaceAll: cr with: lf.\r)",
 "public definition = (\r	^column: {\r		headingDefinition.\r		minorClassHeadingBlock: (label: 'Classes').\r		column: (subject classMirror instanceSide nestedClasses collect: [:ea | nestedClass: ea]).\r		minorClassHeadingBlock: (label: 'Methods').\r		column: (subject classMirror instanceSide methods collect: [:ea | method: ea]).\r		minorClassHeadingBlock: (label: 'Class Methods').\r		column: (subject classMirror classSide methods collect: [:ea | method: ea]).\r	}\r)",
 "expandedHeadingDefinition = (\r	| parts |\r	parts:: List new.\r	subject enclosingClassSubjects\r		do: [:ecs | parts add: (link: ecs name action: [enterSubject:: ecs])]\r		separatedBy: [parts add: ( label: ' in ')].\r	^column: {\r		blank: 5.\r		row: parts.\r		inspectSelf.\r		label: 'Slots'.\r		row: {\r			mediumBlank.\r			column: (subject classMirror instanceSide slots collect: [:ea | label: ea name]).\r		}.\r	}\r)",
 "headingDefinition = (\r	^(column: {\r		expanded: [expandedHeadingDefinition]\r		collapsed: [collapsedHeadingDefinition].\r	}) color: (Color h: 240 s: 0.05 v: 0.9).\r)",
 "inspectSelf = (\r	^row:{filler. link:  'Inspect Presenter' action: [enterSubject:: ObjectSubject onModel: (ObjectMirror reflecting: self)]}\r)",
 "method: mm <MethodMirror> = (\r	| toggle |\r	toggle:: \r		collapsed: [(link: mm name action: [toggle expand]) color: Color black]\r		expanded: [column: {\r			(link: mm name action: [toggle collapse]) color: Color black.\r			TextEditorFragment new\r				text: (crToLf: mm source);\r				acceptResponse: [:ed | \r					| b = MixinBuilder reflecting: mm definingMixin reflectee. |\r					b methods addFromSource: ed textBeingAccepted.\r					b declaration install.\r					ed defaultAcceptResponse.\r				]\r		}].\r	^toggle\r)",
 "minorClassHeadingBlock: body = (\r	^(padded: body with: {10. 5. 5. 5.})\r		color: minorClassHeadingColor\r)",
 "minorClassHeadingColor = (\r	^Gradient \r		from: (Color h: 240 s: 0.02 v: 0.94) \r		to: (Color h: 240 s: 0.02 v: 0.9)\r)",
 "nestedClass: cdm = (\r	^collapsed: [link: cdm simpleName action: [enterSubject:: ClassSubject onModel: cdm]]\r	expanded: [(ClassSubject onModel: cdm) presenter]\r)",
