 "public enclosingClassSubjects = (\r	| them = List new. c |\r	c:: model.\r	[c isNil] whileFalse: [them add: (ClassSubject onModel: c). c:: c enclosingClass].\r	^them\r)",
 "public name = (\r	^model simpleName\r)",
 "public title = (\r	^name\r)",
 "class Namespace basedOn: ns = (\r(* A basic, single namespace for the IDE. Needs to be generalized to support multiple namespaces. *)|\r  platformNamespace = ObjectMirror reflecting: ns.\r  map = Map new.\r|)",
 " ",
 " ",
 "public at: s <Symbol> = (\r	^at: s ifAbsent: [nil]\r)",
 "public at: s <Symbol> ifAbsent: blk= (\r	^map at: s ifAbsent:[(platformNamespace evaluate: s ifCompilerError:[] ifError: blk) reflectee]\r)",
 "public at: s <Symbol> put: v = (\r	^map at: s put: v.\r)",
 "class ObjectPresenter onSubject: s = Presenter onSubject: s (|\r	resultHolder\r|)",
 "onSubject: s",
 "onSubject: s",
 "captionBar: body = (\r	^(column: {\r		smallBlank.\r		row: {\r			mediumBlank.\r			body elasticity: 1.\r			smallBlank\r			}.\r	}) color: captionColor\r)",
 "classInfoLine = (\r	^row: {\r		(label: 'class') width: 0 elasticity: 1.\r		(row: {\r			link: subject className action: [enterSubject: subject classSubject].\r		}) width: 0 elasticity: detailAreaRatio.\r	}\r)",
 "public definition = (\r	^column: {\r		captionBar: (column: {\r			selfInfoLine.\r			enclosingInfoLine.\r			classInfoLine.\r			smallBlank.\r			inspectSelf.\r			TextEditorFragment new\r				changeResponse: [:editor | evaluate: editor textBeingAccepted asString].\r			smallBlank.\r			resultHolder:: holder: [nothing].\r		}).\r		objectDetails.\r	}\r)",
 "enclosingInfoLine = (\r	^row: {\r		(label: 'enclosingObject') width: 0 elasticity: 1.\r		(row: {\r			link: subject enclosingObjectName action: [enterSubject:: ObjectSubject onModel: subject classMirror enclosingObject].\r		}) width: 0 elasticity: detailAreaRatio.\r	}\r)",
 "evaluate: expression = (\r	| s |\r	s:: subject objectMirror \r		evaluate: (withoutNbsp: expression)\r		ifCompilerError: [:e | ^resultHolder content: (label: 'compile-time error: ', e printString)]\r		ifError: [:e | ^resultHolder content: (label: 'runtime error: ', e printString)].\r	resultHolder content: \r		(link: s reflectee printString\r		action: [enterSubject:: ObjectSubject onModel: s]).\r)",
 "goToSelf = (\r	enterSubject: (ObjectSubject onModel: subject objectMirror)\r)",
 "inspectSelf = (\r	^row:{filler. link:  'Inspect Presenter' action: [enterSubject:: ObjectSubject onModel: (ObjectMirror reflecting: self)]}\r)",
 "objectDetails = (\r	^row: {\r	mediumBlank.\r	(column: ((subject classMirror) slots collect: [:slot |\r		| val = subject objectMirror getSlot: slot name ifFail: []. |\r		row: {\r			(label: slot name) width: 0 elasticity: 1.\r			(row: {\r				link: val reflectee printString action: [enterSubject:: ObjectSubject onModel: val].\r			}) width: 0 elasticity: detailAreaRatio.\r		}\r	])) elasticity: 1.\r	smallBlank\r	}\r)",
 "selfCaption = (\r	^'self'\r)",
 "selfInfoLine = (\r	^row: {\r		(label: selfCaption (*asText allBold*)) width: 0 elasticity: 1.\r		(row: {\r			(link: subject title action: [goToSelf]) compressibility: 1.\r			filler compressibility: 0.\r			mediumBlank.\r			(*dropDownMenu: [actionsMenu]*)\r		}) width: 0 elasticity: detailAreaRatio.\r	}\r)",
 "smallBlank = (\r	^blank: 5\r)",
 "withoutNbsp: string = (\r	|\r	nonbreakingSpace = [String fromCharCode: 160] on: Error do: [String value: 160].\r	space = [String fromCharCode: 32] on: Error do: [String value: 32].\r	|\r	^string replaceAll: nonbreakingSpace with: space.\r)",
 "public class ObjectSubject onModel: m = Subject onModel: m (|\r|)",
 "onModel: m",
 "onModel: m",
 "public classMirror ^<ClassMirror> = (\r	^model getClassIfFail: [halt]\r)",
 "public className ^<String> = (\r	^classMirror simpleName\r)",
 "public classSubject = (\r	^ClassSubject onModel: classMirror mixin declaration\r)",
 "public createPresenter = (\r	^ObjectPresenter onSubject: self\r)",
 "public enclosingObjectName = (\r	^classMirror enclosingObject reflectee printString\r)",
 "public objectMirror ^<ObjectMIrror> = (\r	^model\r)",
 "public title = (\r	^model reflectee printString\r)"
